window.addEventListener("load",function(e){initSliders()});document.addEventListener('DOMContentLoaded',()=>{const accrodions=document.querySelectorAll('.question-awswer__item')
accrodions.forEach(el=>{el.addEventListener('click',(e)=>{const self=e.currentTarget;const control=self.querySelector('.question-awswer__control');const content=self.querySelector('.question-awswer__content');self.classList.toggle('_open');if(self.classList.contains('_open')){control.setAttribute('aria-expanded',!0);content.setAttribute('aria-hidden',!1);content.style.maxHeight=content.scrollHeight+'px'}else{control.setAttribute('aria-expanded',!1);content.setAttribute('aria-hidden',!0);content.style.maxHeight=null}})})});const animItems=document.querySelectorAll('._anim-items');if(animItems.length>0){window.addEventListener('scroll',animOnScroll);function animOnScroll(params){for(let index=0;index<animItems.length;index++){const animItem=animItems[index];const animItemHeight=animItem.offsetHeight;const animItemOffset=offset(animItem).top;const animStart=4;let animItemPoint=window.innerHeight-animItemHeight/animStart;if(animItemHeight>window.innerHeight){animItemPoint=window.innerHeight-window.innerHeight/animStart}
if((pageYOffset>animItemOffset-animItemPoint)&&pageYOffset<(animItemOffset+animItemHeight)){animItem.classList.add('_active')}else{if(!animItem.classList.contains('_anim-no-hide')){animItem.classList.remove('_active')}}}}
function offset(el){const rect=el.getBoundingClientRect(),scrollLeft=window.pageXOffset||document.documentElement.scrollLeft,scrollTop=window.pageYOffset||document.documentElement.scrollTop;return{top:rect.top+scrollTop,left:rect.left+scrollLeft}}
setTimeout(()=>{animOnScroll()},300)}